      clk_100MHz
   )
   -----------------------------------------------------------------
   BEGIN
      IF (clk_100MHz'EVENT AND clk_100MHz = '1') THEN
         IF (ExpReset = '1') THEN
            current_state <= s0;
            -- Default Reset Values
            tri_phase_cld <= '0';
            CntDn <= (others => '0');
         ELSE
            current_state <= next_state;

            -- Combined Actions
            CASE current_state IS
               WHEN s0 => 
                  tri_phase_cld <= '0';
                  IF (Run = '1') THEN 
                     tri_phase_cld <= '0';
                  END IF;
               WHEN s2 => 
