                         tri_start = '1') THEN 
                     tri_phase_cld <= '1';
                     CntDn <= PhsCount - 2;
                  ELSIF (CntDn = X"0000") THEN 
                     tri_phase_cld <= '1';
                  END IF;
               WHEN s4 => 
